// Extract the message from webhook data
const userMessage = $input.item.json.body.message;
const userId = $input.item.json.body.userId;
const timestamp = $input.item.json.body.timestamp;

// Simple response logic for demo
let response = "";

if (userMessage.toLowerCase().includes('hello') || userMessage.toLowerCase().includes('hi')) {
  response = "Hello! How can I help you today?";
} else if (userMessage.toLowerCase().includes('weather')) {
  response = "I'd normally check the weather for you, but this is a demo response. In a real setup, I'd integrate with weather APIs!";
} else if (userMessage.toLowerCase().includes('help')) {
  response = "I'm here to help! This is a demonstration of an n8n-powered chat system with token-based usage tracking.";
} else if (userMessage.toLowerCase().includes('tokens')) {
  response = "This system uses tokens to meter usage. Each message consumes one token. You can see your remaining tokens in the UI!";
} else {
  response = `I received your message: "${userMessage}". This is a demo response from the n8n workflow. In a real implementation, this could integrate with AI services, databases, or any other systems.`;
}

// Return structured response
return {
  output: response,
  userId: userId,
  originalMessage: userMessage,
  processedAt: new Date().toISOString(),
  demoNote: "This response was generated by n8n workflow"
};
